import './App.css';
import { useState, useEffect } from "react";
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import Header from './components/Header.js';

function App() {
  const bots = ["ApexArchaeology", "ApexAssistant", "ApexCrafting", "ApexDivination", "ApexSmithing"]
  const [selectedBot, setSelectedBot] = useState("");

  const selectBotOnClick = (botName) => {
    setSelectedBot(botName);
  };

  const [ markdownContent, setMarkdownContent] = useState({md: ""});

  useEffect(()=> {
      fetch(process.env.PUBLIC_URL + '/ApexArchaeology.md')
          .then((res) => res.text())
          .then((md) => {
            setMarkdownContent({ md })
          })
  }, [])

  return (
    <div>
      <Header bots={bots} selectedBot={selectedBot} clickEvent={selectBotOnClick}/>
      <div className="App">
        <ReactMarkdown className="markdown" children={markdownContent.md} remarkPlugins={[remarkGfm]} />
      </div>
    </div>
  );
}

export default App;
